// Set the look-back period (in hours) and buffer (in minutes)
const lastPollHours = 6; // 6 hours
const bufferMinutes = 15; // 15 minutes buffer

// Calculate the threshold date
const lastPollTime = new Date(Date.now() - (lastPollHours * 60 + bufferMinutes) * 60 * 1000);

// Filter only videos published after the threshold
const newItems = items.filter(item => {
  const publishedDate = new Date(item.json.isoDate);
  return publishedDate > lastPollTime;
});

// Map the filtered items to extract video info
const videos = newItems.map(item => {
  let videoId = "";

  // Extract YouTube video ID from 'id' or 'link'
  if (item.json.id?.startsWith("yt:video:")) {
    videoId = item.json.id.replace("yt:video:", "");
  } else if (item.json.link) {
    const match = item.json.link.match(/v=([^&]+)/);
    if (match) videoId = match[1];
  }

  return {
    json: {
      channel: item.json.author || "", // channel name for multiple channels
      title: item.json.title || "",
      videoId,
      link: item.json.link || "",
      isoDate: item.json.isoDate || ""
    }
  };
});

// Return the final array
return videos;

return items.map(item => {
  const transcripts = item.json?.data?.transcripts || {};
  let transcriptBlocks = [];

  // Try to auto-detect any transcript language available
  const langKeys = Object.keys(transcripts);
  for (const key of langKeys) {
    if (transcripts[key]?.custom?.length) {
      transcriptBlocks = transcripts[key].custom;
      break;
    }
  }

  if (!transcriptBlocks.length) {
    return { json: { transcript: "", error: "No transcript found in any language" } };
  }

  const combinedText = transcriptBlocks
    .map(block => block.text?.trim() || "")
    .join(" ");

  return {
    json: {
      transcript: combinedText
    }
  };
});

